#include <stdio.h>
#include <stdlib.h>

int main()
{
    // Problema 8 do Projeto Euler: Encontre o produto de treze dígitos consecutivos no número de 1000 dígitos que tem o maior produto.

    // Este é o número de 1000 dígitos fornecido pelo problema.
    char numero[1001] = "";

    // Inicializando as variáveis que armazenarão o produto dos números adjacentes e o maior produto encontrado.
    long long int NumeuroProximo = 0;
    long long int MaiorNumeroProximo = 0;
    int i, j;

    // Loop para percorrer o número de 1000 dígitos.
    for (i = 0; i < 1000 - 13; i++) {
        // Inicializando o produto dos números adjacentes.
        NumeuroProximo = 1;
        // Loop para multiplicar 13 números adjacentes.
        for (j = 0; j < 13; j++) {
            NumeuroProximo *= numero[i + j] - '0'; // Convertendo o caractere para inteiro e multiplicando.
        }
        // Se o produto atual for maior que o maior produto encontrado, atualize o maior produto.
        if (NumeuroProximo > MaiorNumeroProximo) {
            MaiorNumeroProximo = NumeuroProximo;
        }
    }
    // Imprimindo o maior produto de 13 dígitos adjacentes.
    printf("O produto máximo de 13 dígitos adjacentes é: %lli\n", MaiorNumeroProximo);

    return 0;
}
